; solution for day 1, part 1
; in 6502 assembly

define SCORE_LOW $00
define SCORE_HI $01
define ENTRY_LOW $02
define ENTRY_HI $03
define MOVE_SCORE $04
define OPP $10
define ME $11
define DEBUG $20

LDA #$93
STA ENTRY_LOW
LDA #$06
STA ENTRY_HI
JSR loop

get_move:
  LDA #$00
  STA OPP
  STA ME
  TAX
  ; get the next round
opponent:
  LDY #$00
  LDA (ENTRY_LOW), Y ; get the index
  CMP #$00	; compare A to 0
  BEQ end	; if equal, end of list reached
  STA OPP	; first number is opponents move
  INY
  LDA (ENTRY_LOW), Y
  STA ME

  CLC
  LDA ENTRY_LOW
  ADC #$02
  STA ENTRY_LOW
  BCC ctrl_flow	; if no carry flag set, continue control flow
  INC ENTRY_HI ; 
ctrl_flow:
  LDA ME
  CMP OPP	; compare A (my move) to the move in $OPP
  BEQ draw	; if same, it's a draw

define_opponent_move:
  LDA #$01	; test for opponent move (rock)
  CMP OPP
  BEQ rock
  LDA #$02
  CMP OPP
  BEQ paper
  LDA #$03
  CMP OPP
  BEQ scissors
  LDY #$fe	; weird. jump to end, because only 1,2,3 are valid.
  JMP end  

win:    
    ; what to do when I win
    LDA #$06
    STA MOVE_SCORE
    JMP update_score

lose:
    ; what to do when I lose
    LDA #$00
    STA MOVE_SCORE
    JMP update_score

rock:
    ; opponent has rock
    LDA #$02
    CMP ME
    BEQ win
    JMP lose

paper:
    ; opponent has paper
    LDA #$03
    CMP ME
    BEQ win
    JMP lose

scissors:
    ; opponent has scissors
    LDA #$01
    CMP ME
    BEQ win
    JMP lose

draw:
    ; what to do when we draw
    CLC
    LDA #$03
    STA MOVE_SCORE

update_score:
    ; update the score
    CLC
    LDA SCORE_LOW
    ADC ME
    STA SCORE_LOW
    BCC move_score
    INC SCORE_HI
move_score:
    CLC
    LDA SCORE_LOW
    ADC MOVE_SCORE
    STA SCORE_LOW
    BCC loop
    INC SCORE_HI

loop:
    ; main loop here
    JMP get_move

end:
   LDA SCORE_LOW
   BRK

